sys_data_source.data_loader:
(function loadData(import_set_table) {

    //import_set_table.addJSONColumn("data", 5000);
    // Define Data Stream Action Inputs
    var inputs = {};
    var grConnectionAlias = new GlideRecord('sys_alias');
    grConnectionAlias.get('c69242a587c7d110c679edfd0ebb3590');
    inputs['connection_alias'] = grConnectionAlias;

    try {

        // Execute Data Stream Action.
        var result = sn_fd.FlowAPI.getRunner().datastream('x_rhtpp_sg_rh_ins.get_red_hat_insights_inventory_data').inForeground().withInputs(inputs).run();
        var stream = result.getDataStream();
        // Process each item in the data stream
        while (stream.hasNext()) {
            // Get a single item from the data stream.
            var item = stream.next();
            // Use the item.
            var map = {};
            map['u_fqdn'] = item.fqdn;
			map['u_name'] = item.name;
			map['u_os_release'] = item.os_release;
			map['u_object_id'] = item.object_id;
            import_set_table.insert(map);
        }

    } catch (ex) {

        var message = ex.getMessage();

        gs.error(message);

    } finally {

        stream.close();

    }

})(import_set_table);

