sys_variable_value.value:
(function parse(inputs, outputs) {

// Source item string is available in inputs.sourceItem.
// Generate output by setting fields of outputs.targetObject.
// 
// The structure of outputs.targetObject corresponds to the
// structure defined in the Data Stream Action output.
// 
// Optionally skip an item in the stream by setting
// outputs.state = 'SKIP'
//
// JSON Example:
// var item = JSON.parse(inputs.sourceItem);
// outputs.targetObject.ffName = item.firstName;
// outputs.targetObject.llName = item.lastName;
// outputs.targetObject.address.home.city = item.address.home.city;


var record = JSON.parse(inputs.sourceItem);
outputs.targetObject.name = record["display_name"];
outputs.targetObject.sys_uuid = record["system_uuid"]
outputs.targetObject.last_seen = record["last_seen"]
outputs.targetObject.stale_at = record["stale_at"]
outputs.targetObject.hits = record["hits"]
outputs.targetObject.critical_hits = record["critical_hits"]
outputs.targetObject.important_hits = record["important_hits"]
outputs.targetObject.moderate_hits = record["moderate_hits"]
outputs.targetObject.low_hits = record["low_hits"]
outputs.targetObject.inc_hits = record["incident_hits"]
outputs.targetObject.all_path_hits = record["all_pathway_hits"]
outputs.targetObject.path_filter_hits = record["pathway_filter_hits"]
outputs.targetObject.rhel_version = record["rhel_version"]

})(inputs, outputs);

